import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import styles from './LoginForm.module.css'; // Use the same styles as LoginForm
import loginImage from '/Users/pcompany/Desktop/Sem_VII/ET/AgriDost/frontend/src/components/Auth/loginimage.jpg'; // Update the image import

const SignUpForm = () => {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [emailError, setEmailError] = useState("");
    const [passwordError, setPasswordError] = useState("");
    const navigate = useNavigate(); // Initialize useNavigate

    const validateEmail = (email) => {
        const pattern = /^[^ ]+@[^ ]+\.[a-z]{2,3}$/;
        return pattern.test(email);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        let valid = true;

        if (!email) {
            setEmailError("Email can't be blank");
            valid = false;
        } else if (!validateEmail(email)) {
            setEmailError("Enter a valid email address");
            valid = false;
        } else {
            setEmailError("");
        }

        if (!password) {
            setPasswordError("Password can't be blank");
            valid = false;
        } else {
            setPasswordError("");
        }

        if (valid) {
            // Handle the signup logic here (e.g., API call)
            alert(`Welcome, ${username}!`);
            navigate('/'); // Redirect to home or another page after signup
        }
    };

    return (
        <div className={styles.container}>
            <div className={styles.leftSide}>
                <img src={loginImage} alt="Building" />
                <div className={styles.wrapper}>
                    <header>Sign Up</header>
                    <form onSubmit={handleSubmit}>
                        <div className={`${styles.field}`}>
                            <label htmlFor="username">Username</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="username"
                                    type="text" 
                                    placeholder="Enter your username" 
                                    value={username} 
                                    onChange={(e) => setUsername(e.target.value)} 
                                />
                            </div>
                        </div>
                        <div className={`${styles.field} ${emailError ? styles.error : ""}`}>
                            <label htmlFor="email">Email Address</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="email"
                                    type="text" 
                                    placeholder="Enter your email" 
                                    value={email} 
                                    onChange={(e) => setEmail(e.target.value)} 
                                />
                            </div>
                            {emailError && <div className={styles.errorTxt}>{emailError}</div>}
                        </div>
                        <div className={`${styles.field} ${passwordError ? styles.error : ""}`}>
                            <label htmlFor="password">Password</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="password"
                                    type="password" 
                                    placeholder="Enter your password" 
                                    value={password} 
                                    onChange={(e) => setPassword(e.target.value)} 
                                />
                            </div>
                            {passwordError && <div className={styles.errorTxt}>{passwordError}</div>}
                        </div>
                        <button type="submit">Sign Up</button>
                    </form>
                    <div className={styles.signTxt}>Already a member? <a href="/login">Login now</a></div>
                </div>
            </div>
        </div>
    );
};

export default SignUpForm;
