import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css'; // Import Navbar CSS

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [selectedUser, setSelectedUser] = useState(''); // Default empty user
  const navigate = useNavigate();

  // Check if user is logged in by checking localStorage
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');  // Fetch stored username

    if (token && storedUsername) {
      setIsLoggedIn(true);
      setSelectedUser(storedUsername); // Set the selected user
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');  // Remove token from localStorage
    localStorage.removeItem('username');  // Remove username from localStorage
    setIsLoggedIn(false); // Set login state to false
    navigate('/'); // Redirect to home page after logout
    window.location.reload(); // Reload page to update the UI
  };

  // Handle user switch
  const handleUserChange = (user) => {
    setSelectedUser(user);
    localStorage.setItem('username', user); // Update the stored username
    // Redirect or refresh to fetch data for the new user
    navigate('/farm-dashboard'); // Ensure this navigates to the farm dashboard
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <img src="/images/logo.png" alt="Logo" className="navbar-logo" />
      </div>
      <ul className="navbar-center">
        <li><Link to="/">About Us</Link></li>
        <li><Link to="/">Sensors</Link></li>
        <li><Link to="/farm-dashboard">Farm Data</Link></li>
      </ul>
      <div className="navbar-right">
        {isLoggedIn ? (
          <>
            <div className="navbar-user-dropdown">
              <button className="navbar-user-button">{selectedUser || 'Select User'}</button>
              <div className="navbar-user-dropdown-content">
                {['Ansh Patel', 'Tarsh Agarwal'].map(user => (
                  <button key={user} onClick={() => handleUserChange(user)} className="navbar-dropdown-item">
                    {user}
                  </button>
                ))}
              </div>
            </div>
            <button onClick={handleLogout} className="navbar-logout">Logout</button>
          </>
        ) : (
          <>
            <Link to="/signup">
              <button className="navbar-signup">Sign up</button>
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
