import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import axios from 'axios'; // Import axios for making HTTP requests
import styles from './LoginForm.module.css'; // Correct import path for CSS
import loginImage from './loginimage.jpg'; // Correct import path for image

const SignUpForm = () => {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [emailError, setEmailError] = useState("");
    const [passwordError, setPasswordError] = useState("");
    const [serverError, setServerError] = useState(""); // New state for server error
    const navigate = useNavigate(); // Initialize useNavigate

    const validateEmail = (email) => {
        const pattern = /^[^ ]+@[^ ]+\.[a-z]{2,3}$/;
        return pattern.test(email);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        let valid = true;

        // Reset errors
        setEmailError("");
        setPasswordError("");
        setServerError("");

        // Validate form inputs
        if (!email) {
            setEmailError("Email can't be blank");
            valid = false;
        } else if (!validateEmail(email)) {
            setEmailError("Enter a valid email address");
            valid = false;
        }

        if (!password) {
            setPasswordError("Password can't be blank");
            valid = false;
        }

        if (valid) {
            try {
                // Handle the signup logic here (e.g., API call)
                const response = await axios.post('/api/users/signup', { name: username, email, password });

                if (response.status === 201) {
                    const { token } = response.data;

                    // Check if the token exists in the response
                    if (token) {
                        localStorage.setItem('token', token); // Store token in localStorage
                        console.log('Token stored:', token); // Debugging
                        alert(`Welcome, ${username}!`);
                        navigate('/'); // Redirect to home or another page after signup
                    } else {
                        // Handle case where token is not returned
                        setServerError('Token not received from server.');
                    }
                } else {
                    // Handle unexpected status codes
                    setServerError('Unexpected response from the server.');
                }
            } catch (error) {
                // Enhanced error handling
                if (error.response) {
                    // The request was made and the server responded with a status code
                    // that falls out of the range of 2xx
                    console.error('Error response:', error.response);
                    setServerError(error.response.data.message || 'An error occurred during signup. Please try again.');
                } else if (error.request) {
                    // The request was made but no response was received
                    console.error('Error request:', error.request);
                    setServerError('No response from the server. Please try again later.');
                } else {
                    // Something happened in setting up the request that triggered an Error
                    console.error('Error message:', error.message);
                    setServerError('Error setting up the request. Please try again.');
                }
            }
        }
    };

    return (
        <div className={styles.container}>
            <div className={styles.leftSide}>
                <img src={loginImage} alt="Signup" className={styles.loginImage} />
                <div className={styles.wrapper}>
                    <header>Sign Up</header>
                    <form onSubmit={handleSubmit}>
                        <div className={`${styles.field}`}>
                            <label htmlFor="username">Username</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="username"
                                    type="text" 
                                    placeholder="Enter your username" 
                                    value={username} 
                                    onChange={(e) => setUsername(e.target.value)} 
                                />
                            </div>
                        </div>
                        <div className={`${styles.field} ${emailError ? styles.error : ""}`}>
                            <label htmlFor="email">Email Address</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="email"
                                    type="text" 
                                    placeholder="Enter your email" 
                                    value={email} 
                                    onChange={(e) => setEmail(e.target.value)} 
                                />
                            </div>
                            {emailError && <div className={styles.errorTxt}>{emailError}</div>}
                        </div>
                        <div className={`${styles.field} ${passwordError ? styles.error : ""}`}>
                            <label htmlFor="password">Password</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="password"
                                    type="password" 
                                    placeholder="Enter your password" 
                                    value={password} 
                                    onChange={(e) => setPassword(e.target.value)} 
                                />
                            </div>
                            {passwordError && <div className={styles.errorTxt}>{passwordError}</div>}
                        </div>
                        {serverError && <div className={styles.serverError}>{serverError}</div>} {/* Display server error */}
                        <button type="submit">Sign Up</button>
                    </form>
                    <div className={styles.signTxt}>Already a member? <a href="/login">Login now</a></div>
                </div>
            </div>
        </div>
    );
};

export default SignUpForm;
