import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './FarmDashboard.css'; // FarmDashboard CSS file

const FarmDashboard = () => {
  const [farmData, setFarmData] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('Ansh Patel'); // Default username

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');
    const storedUserId = localStorage.getItem('userId');

    if (token && storedUsername && storedUserId) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    } else {
      // Default login if no token exists
      localStorage.setItem('token', 'default-token'); // Set a default token
      localStorage.setItem('username', 'Ansh Patel');
      localStorage.setItem('userId', 'default-user-id'); // Set a default user ID
      setIsLoggedIn(true);
      setUsername('Ansh Patel');
    }

    const fetchFarmData = async () => {
      try {
        const token = localStorage.getItem('token'); // Retrieve the token from localStorage
        if (!token) {
          console.error('No token found'); // Log an error if token is missing
          return; // Exit if no token is found
        }

        const response = await axios.get('/api/farm', {
          headers: { 'Authorization': `Bearer ${token}` } // Include token in the request headers
        });
        setFarmData(response.data);
      } catch (error) {
        console.error('Error fetching farm data', error);
      }
    };

    fetchFarmData();
  }, []);

  return (
    <div>
      {/* Navbar implementation */}
      <nav className="navbar">
        <div className="navbar-left">
          <img src="/images/logo.png" alt="Logo" className="navbar-logo" />
        </div>
        <ul className="navbar-center">
          <li><Link to="/">About Us</Link></li>
          <li><Link to="/">Sensors</Link></li>
          <li><Link to="/farm-dashboard">Farm Data</Link></li> {/* Farm Data link */}
        </ul>
        <div className="navbar-right">
          {isLoggedIn ? (
            <>
              <span className="navbar-username">{username}</span> {/* Display username */}
            </>
          ) : (
            <></>
          )}
        </div>
      </nav>

      {/* Farm data content */}
      <div className="farm-dashboard">
        <h2>Farm Data Dashboard</h2>
        {farmData.length > 0 ? (
          farmData.map(data => (
            <div key={data._id} className="farm-data-item">
              <p><strong>Temperature:</strong> {data.temperature} °C</p>
              <p><strong>Humidity:</strong> {data.humidity} %</p>
              <p><strong>Soil Moisture:</strong> {data.soilMoisture}</p>
              <p><strong>Motion Detected:</strong> {data.motionDetected ? 'Yes' : 'No'}</p>
              <p><strong>Timestamp:</strong> {new Date(data.timestamp).toLocaleString()}</p>
            </div>
          ))
        ) : (
          <p>No data available</p>
        )}
      </div>
    </div>
  );
};

export default FarmDashboard;
