// FarmDataChart.js
import React, { useRef, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, TimeScale, Title, Tooltip, Legend, PointElement } from 'chart.js';
import 'chartjs-adapter-date-fns'; // Import the date-fns adapter for handling time scales

// Registering necessary components including PointElement
ChartJS.register(LineElement, CategoryScale, LinearScale, TimeScale, Title, Tooltip, Legend, PointElement);

const FarmDataChart = ({ temperatureData, soilMoistureData, humidityData, motionDetectionData }) => {
  // Create a ref to hold the chart instance
  const chartRef = useRef(null);

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            let label = context.dataset.label || '';
            if (label) {
              label += ': ';
            }
            if (context.parsed.y !== null) {
              label += context.parsed.y;
            }
            return label;
          }
        }
      }
    },
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
          tooltipFormat: 'll', // Format for the tooltip (e.g., "Sep 25, 2024")
        },
        title: {
          display: true,
          text: 'Date'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Value'
        }
      }
    }
  };

  const data = {
    datasets: [
      {
        label: 'Temperature',
        data: temperatureData,
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderWidth: 1
      },
      {
        label: 'Soil Moisture',
        data: soilMoistureData,
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        borderWidth: 1
      },
      {
        label: 'Humidity',
        data: humidityData,
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderWidth: 1
      },
      {
        label: 'Motion Detection',
        data: motionDetectionData,
        borderColor: 'rgba(255, 159, 64, 1)',
        backgroundColor: 'rgba(255, 159, 64, 0.2)',
        borderWidth: 1,
        yAxisID: 'y1'
      }
    ]
  };

  useEffect(() => {
    // Cleanup function to destroy chart instance when component unmounts
    return () => {
      if (chartRef.current) {
        chartRef.current.destroy();
      }
    };
  }, []);

  return (
    <div>
      <Line
        options={options}
        data={data}
        ref={chartRef} // Assign ref to the chart instance
      />
    </div>
  );
};

export default FarmDataChart;
