import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import GraphComponent from './GraphComponent';
import './FarmDashboard.css';

const FarmDashboard = () => {
  const [farmData, setFarmData] = useState(Array(30).fill({
    timestamp: new Date().toISOString(),
    temperature: null,
    soilMoisture: null,
    humidity: null,
    motionDetected: null,
  }));
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('Ansh Patel');

  // Placeholder state for latest data to display in cards
  const [latestTemperature, setLatestTemperature] = useState(null);
  const [latestSoilMoisture, setLatestSoilMoisture] = useState(null);
  const [latestHumidity, setLatestHumidity] = useState(null);
  const [latestMotionDetected, setLatestMotionDetected] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
    const storedUsername = localStorage.getItem('username');

    if (token && userId) {
      setIsLoggedIn(true);
      setUsername(storedUsername || 'Ansh Patel');
    } else {
      localStorage.setItem('token', 'default-token');
      localStorage.setItem('username', 'Ansh Patel');
      localStorage.setItem('userId', 'default-user-id');
      setIsLoggedIn(true);
      setUsername('Ansh Patel');
    }

    const fetchFarmData = async () => {
      try {
        if (!token || !userId) {
          console.error('No token or userId found');
          return;
        }

        if (userId.length !== 24) {
          console.error('Invalid User ID format');
          return;
        }

        const response = await axios.get(`/api/farm/${userId}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        const newData = response.data;
        if (Array.isArray(newData)) {
          const latestData = newData[newData.length - 1];
          setLatestTemperature(latestData.temperature);
          setLatestSoilMoisture(latestData.soilMoisture);
          setLatestHumidity(latestData.humidity);
          setLatestMotionDetected(latestData.motionDetected);

          // Update graph data with new data while keeping it within 30 points
          setFarmData(prevData => {
            const updatedData = [...prevData, latestData].slice(-30);
            return updatedData;
          });
        } else if (newData) {
          setLatestTemperature(newData.temperature);
          setLatestSoilMoisture(newData.soilMoisture);
          setLatestHumidity(newData.humidity);
          setLatestMotionDetected(newData.motionDetected);

          setFarmData(prevData => {
            const updatedData = [...prevData, newData].slice(-30);
            return updatedData;
          });
        }
      } catch (error) {
        console.error('Error fetching farm data', error);
      }
    };

    fetchFarmData();

    const interval = setInterval(fetchFarmData, 60000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-left">
          <Link to="/">
            <img src="/images/logo.png" alt="Logo" className="navbar-logo" />
          </Link>
        </div>
        <ul className="navbar-center">
          <li><Link to="/">About Us</Link></li>
          <li><Link to="/">Sensors</Link></li>
          <li><Link to="/farm-dashboard">Farm Data</Link></li>
        </ul>
        <div className="navbar-right">
          {isLoggedIn ? <span className="navbar-username">{username}</span> : null}
        </div>
      </nav>

      {/* Sensor Cards */}
      <div className="row">
        {/* Temperature, Soil Moisture, Humidity, Motion Detection cards */}
        {/* Each card similar to provided with latest data and loading text if no data */}
      </div>

      {/* Farm Data Dashboard Heading */}
      <h2>Farm Data Dashboard</h2>

      {/* Graph Component */}
      <GraphComponent data={farmData} />

      {/* Data Table */}
      <div className="raw-data-table">
        <ul>
          {farmData.map((data, index) => (
            <li key={index}>
              <strong>Date:</strong> {new Date(data.timestamp).toLocaleString()} | 
              <strong> Temperature:</strong> {data.temperature ? `${data.temperature} °C` : 'N/A'} | 
              <strong> Soil Moisture:</strong> {data.soilMoisture ? `${data.soilMoisture} %` : 'N/A'} | 
              <strong> Humidity:</strong> {data.humidity ? `${data.humidity} %` : 'N/A'} | 
              <strong> Motion Detected:</strong> {data.motionDetected ? 'Yes' : 'No'}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default FarmDashboard;
