import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css'; // Import Navbar CSS

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [userOptionsVisible, setUserOptionsVisible] = useState(false);
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  // Check if user is logged in by checking localStorage
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');  // Fetch stored username

    if (token && storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername); // Set the username state
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');  // Remove token from localStorage
    localStorage.removeItem('username');  // Remove username from localStorage
    setIsLoggedIn(false); // Set login state to false
    navigate('/'); // Redirect to home page after logout
    window.location.reload(); // Reload page to update the UI
  };

  // Fetch users from an API or hard-code them here
  useEffect(() => {
    // Mock users for demonstration
    setUsers([
      { name: 'Ansh Patel' },
      { name: 'Tarsh Agarwal' }
    ]);
  }, []);

  const handleUserChange = (userName) => {
    localStorage.setItem('username', userName);
    setUsername(userName);
    setUserOptionsVisible(false);
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <img src="/images/logo.png" alt="Logo" className="navbar-logo" />
      </div>
      <ul className="navbar-center">
        <li><Link to="/">About Us</Link></li>
        <li><Link to="/">Sensors</Link></li>
        <li><Link to="/farm-dashboard">Farm Data</Link></li> {/* Updated Link */}
      </ul>
      <div className="navbar-right">
        {isLoggedIn ? (
          <>
            <div className="navbar-username">
              <button onClick={() => setUserOptionsVisible(!userOptionsVisible)}>
                {username}
              </button>
              {userOptionsVisible && (
                <div className="user-options">
                  {users.map((user) => (
                    <button key={user.name} onClick={() => handleUserChange(user.name)}>
                      {user.name}
                    </button>
                  ))}
                </div>
              )}
            </div>
            <button onClick={handleLogout} className="navbar-logout">Logout</button> {/* Logout button */}
          </>
        ) : (
          <Link to="/signup">
            <button className="navbar-signup">Sign up</button>
          </Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
