import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FarmDashboard.css'; // Import CSS for styling

const FarmDashboard = () => {
  const [farmData, setFarmData] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFarmData = async () => {
      const token = localStorage.getItem('token'); // Retrieve token from localStorage
      if (!token) {
        console.error('No token found'); // Log error if token is not found
        setError('No token found. Please log in.');
        return;
      }

      try {
        const response = await axios.get('/api/farm', {
          headers: { 'Authorization': `Bearer ${token}` } // Include token in the request headers
        });
        setFarmData(response.data);
      } catch (error) {
        console.error('Error fetching farm data:', error);
        setError('Error fetching farm data. Please try again.');
      }
    };

    fetchFarmData();
  }, []);

  return (
    <div className="farm-dashboard">
      <h2>Farm Data Dashboard</h2>
      {error && <p className="error-message">{error}</p>}
      {farmData.length > 0 ? (
        farmData.map(data => (
          <div key={data._id} className="farm-data-item">
            <p><strong>Temperature:</strong> {data.temperature} Â°C</p>
            <p><strong>Humidity:</strong> {data.humidity} %</p>
            <p><strong>Soil Moisture:</strong> {data.soilMoisture}</p>
            <p><strong>Motion Detected:</strong> {data.motionDetected ? 'Yes' : 'No'}</p>
            <p><strong>Timestamp:</strong> {new Date(data.timestamp).toLocaleString()}</p>
          </div>
        ))
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
};

export default FarmDashboard;
