import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('Ansh Patel'); // Default username

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');
    const storedUserId = localStorage.getItem('userId');

    if (token && storedUsername && storedUserId) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    } else {
      localStorage.setItem('token', 'default-token'); // Set a default token
      localStorage.setItem('username', 'Ansh Patel');
      localStorage.setItem('userId', 'default-user-id'); // Set a default user ID
      setIsLoggedIn(true);
      setUsername('Ansh Patel');
    }
  }, []);

  // Scroll to the bottom of the page
  const handleScrollToBottom = () => {
    window.scrollTo({
      top: document.body.scrollHeight,
      behavior: 'smooth'
    });
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/">
          <img src="/images/logo.png" alt="Logo" className="navbar-logo" />
        </Link>
      </div>
      <ul className="navbar-center">
        <li><a href="#about">About Us</a></li>
        <li><a href="#sensors">Sensors</a></li>
        <li>
          <button onClick={handleScrollToBottom} className="navbar-link-button">
            Farm Data
          </button>
        </li>
      </ul>
      <div className="navbar-right">
        {/* Display username if logged in */}
        {isLoggedIn && <span className="navbar-username">{username}</span>}
      </div>
    </nav>
  );
};

export default Navbar;
