import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import styles from './LoginForm.module.css';
import loginImage from '/Users/pcompany/Desktop/Sem_VII/ET/AgriDost/frontend/src/components/Auth/loginimage.jpg'; // Update the import for the image

const LoginForm = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [emailError, setEmailError] = useState("");
    const [passwordError, setPasswordError] = useState("");
    const navigate = useNavigate(); // Initialize useNavigate

    const validateEmail = (email) => {
        const pattern = /^[^ ]+@[^ ]+\.[a-z]{2,3}$/;
        return pattern.test(email);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        let valid = true;

        if (!email) {
            setEmailError("Email can't be blank");
            valid = false;
        } else if (!validateEmail(email)) {
            setEmailError("Enter a valid email address");
            valid = false;
        } else {
            setEmailError("");
        }

        if (!password) {
            setPasswordError("Password can't be blank");
            valid = false;
        } else {
            setPasswordError("");
        }

        if (valid) {
            localStorage.setItem('email', email);
            localStorage.setItem('password', password);
            alert(`Welcome, ${email}!`);
            navigate('/');
        }
    };

    const handleForgotPassword = () => {
        // Replace this with actual navigation to the password recovery page
        alert("Redirecting to password recovery...");
    };

    return (
        <div className={styles.container}>
            <div className={styles.leftSide}>
                <img src={loginImage} alt="Building" />
                <div className={styles.wrapper}>
                    <header>Login</header>
                    <form onSubmit={handleSubmit}>
                        <div className={`${styles.field} ${emailError ? styles.error : ""}`}>
                            <label htmlFor="email">Email Address</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="email"
                                    type="text" 
                                    placeholder="Enter your email" 
                                    value={email} 
                                    onChange={(e) => setEmail(e.target.value)} 
                                />
                            </div>
                            {emailError && <div className={styles.errorTxt}>{emailError}</div>}
                        </div>
                        <div className={`${styles.field} ${passwordError ? styles.error : ""}`}>
                            <label htmlFor="password">Password</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="password"
                                    type="password" 
                                    placeholder="Enter your password" 
                                    value={password} 
                                    onChange={(e) => setPassword(e.target.value)} 
                                />
                            </div>
                            {passwordError && <div className={styles.errorTxt}>{passwordError}</div>}
                        </div>
                        <button type="button" className={styles.forgotPassword} onClick={handleForgotPassword}>
                            Forgot password?
                        </button>
                        <button type="submit">Login</button>
                    </form>
                    <div className={styles.signTxt}>Not yet a member? <a href="/signup">Signup now</a></div>
                </div>
            </div>
        </div>
    );
};

export default LoginForm;
