import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css'; // Import Navbar CSS
import axios from 'axios'; // Import axios for API calls

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('Ansh Patel'); // Default username
  const [userOptionsVisible, setUserOptionsVisible] = useState(false);
  const [users, setUsers] = useState([]); // To store user list
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');

    if (token && storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
      fetchUsers(); // Fetch user list on load
    } else {
      // Default login if no token exists
      localStorage.setItem('token', 'default-token'); // Set a default token or fetch it from a server
      localStorage.setItem('username', 'Ansh Patel');
      setIsLoggedIn(true);
      setUsername('Ansh Patel');
    }
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('/api/users'); // Fetch list of users
      setUsers(response.data); // Store users
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleUserChange = async (user) => {
    localStorage.setItem('username', user.name);
    setUsername(user.name);
    setUserOptionsVisible(false);

    // Optionally fetch data for the selected user
    try {
      const response = await axios.get(`/api/farm-data/${user._id}`);
      // Handle the fetched farm data (e.g., save it in state or localStorage)
    } catch (error) {
      console.error('Error fetching farm data:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    setIsLoggedIn(false);
    navigate('/'); // Redirect to home page after logout
    window.location.reload(); // Reload page to update the UI
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <img src="/images/logo.png" alt="Logo" className="navbar-logo" />
      </div>
      <ul className="navbar-center">
        <li><Link to="/">About Us</Link></li>
        <li><Link to="/">Sensors</Link></li>
        <li><Link to="/farm-dashboard">Farm Data</Link></li> {/* Farm Data link */}
      </ul>
      <div className="navbar-right">
        {isLoggedIn ? (
          <>
            <div className="navbar-username">
              <button onClick={() => setUserOptionsVisible(!userOptionsVisible)}>
                {username}
              </button>
              {userOptionsVisible && (
                <div className="user-options">
                  {users.map(user => (
                    <button key={user._id} onClick={() => handleUserChange(user)}>
                      {user.name}
                    </button>
                  ))}
                </div>
              )}
            </div>
            <button onClick={handleLogout} className="navbar-switch-user">Switch User</button> {/* Switch User button */}
          </>
        ) : (
          <></> // No Sign Up button
        )}
      </div>
    </nav>
  );
};

export default Navbar;
