import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import './Navbar.css'; // Import Navbar CSS
import AboutUs from './AboutUs'; // Import AboutUs component

// Navbar component styled to look like the provided image
const Navbar = () => {
  return (
    <nav className="navbar">
      <div className="navbar-left">
        <img src="/images/logo.png" alt="Logo" className="navbar-logo" />
      </div>
      <ul className="navbar-center">
        <li><Link to="/">About Us</Link></li>
        <li><Link to="/">Sensors</Link></li>
        <li><Link to="/farm-dashboard">Farm Data</Link></li> {/* Updated Link */}
      </ul>
      <div className="navbar-right">
        <button className="navbar-search">
          <span role="img" aria-label="search">üîç</span>
        </button>
        <Link to="/signup">
          <button className="navbar-signup">Sign up</button>
        </Link>
      </div>
    </nav>
  );
};

// UserList component to fetch and display users
const UserList = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        // Update the URL if necessary
        const response = await axios.get('/api/users');
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching users:', error.response ? error.response.data : error.message);
      }
    };
    fetchUsers();
  }, []);

  return (
    <div>
      <Navbar />
      {/* Display About Us section below Navbar */}
      <AboutUs />

      <div className="user-list">
        <h2>User List</h2>
        <ul>
          {users.length > 0 ? (
            users.map(user => (
              <li key={user._id}>{user.name} - {user.email}</li>
            ))
          ) : (
            <li>No users found.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default UserList;
