import React from 'react';
import './Card.css';  // Ensure this file exists

function Card() {
  const cards = [
    {
      title: "Soil Moisture Sensor",
      description: "A soil moisture sensor measures the volumetric water content in soil.",
      image: "/Users/pcompany/Desktop/Sem_VII/ET/AgriDost/frontend/public/images/soil moisture.png", // Updated path
      link: "https://en.wikipedia.org/wiki/Soil_moisture_sensor"
    },
    {
      title: "Temperature and Humidity Sensor",
      description: "A temperature and humidity sensor measures temperature and relative humidity.",
      image: "/images/temperature-and-humidity.png", // Updated path
      link: "https://en.wikipedia.org/wiki/Thermo-hygrograph"
    },
    {
      title: "Motion Detector",
      description: "A motion detector detects movement in an area, often used for security purposes.",
      image: "/images/motion-detector.png", // Updated path
      link: "https://en.wikipedia.org/wiki/Passive_infrared_sensor"
    }
  ];

  return (
    <div className="card-list">
      {cards.map((card, index) => (
        <a key={index} href={card.link} className="card-item" target="_blank" rel="noopener noreferrer">
          <img src={card.image} alt={card.title} />
          <span className={card.title.toLowerCase().replace(/ /g, '-')}>{card.title}</span>
          <h3>{card.description}</h3>
          <div className="arrow">
            <i className="fas fa-arrow-right card-icon"></i>
          </div>
        </a>
      ))}
    </div>
  );
}

export default Card;
