import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import './FarmDashboard.css'; // Import CSS for styling

// Register Chart.js components and Filler plugin
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const FarmDashboard = () => {
  const [farmData, setFarmData] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('Ansh Patel'); // Default username

  // Latest data state for each sensor
  const [latestTemperature, setLatestTemperature] = useState(null);
  const [latestSoilMoisture, setLatestSoilMoisture] = useState(null);
  const [latestHumidity, setLatestHumidity] = useState(null);
  const [latestMotionDetected, setLatestMotionDetected] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token'); // Retrieve the token from localStorage
    const userId = localStorage.getItem('userId'); // Retrieve the userId from localStorage
    const storedUsername = localStorage.getItem('username');

    if (token && userId) {
      setIsLoggedIn(true);
      setUsername(storedUsername || 'Ansh Patel'); // Set the username if available
    } else {
      // If no token or userId exists, simulate a default user
      localStorage.setItem('token', 'default-token'); // Set a default token
      localStorage.setItem('username', 'Ansh Patel');
      localStorage.setItem('userId', 'default-user-id'); // Set a default user ID
      setIsLoggedIn(true);
      setUsername('Ansh Patel');
    }

    const fetchFarmData = async () => {
      try {
        const token = localStorage.getItem('token'); // Re-fetch in case it changes
        const userId = localStorage.getItem('userId'); // Re-fetch in case it changes

        if (!token || !userId) {
          console.error('No token or userId found');
          return;
        }

        // Validate that userId is a proper MongoDB ObjectId (24 hex characters)
        if (userId.length !== 24) {
          console.error('Invalid User ID format');
          return;
        }

        const response = await axios.get(`/api/farm/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = response.data;
        setFarmData(data);

        // Debugging: Log the fetched data
        console.log('Fetched Farm Data:', data);

        // Set latest sensor data
        if (Array.isArray(data) && data.length > 0) {
          const latestData = data[data.length - 1]; // Assume last item is the latest
          setLatestTemperature(latestData.temperature);
          setLatestSoilMoisture(latestData.soilMoisture);
          setLatestHumidity(latestData.humidity);
          setLatestMotionDetected(latestData.motionDetected);

          console.log('Latest Data:', latestData);
        }
      } catch (error) {
        console.error('Error fetching farm data', error);
      }
    };

    fetchFarmData();

    // Set interval to fetch the latest data every minute (60000ms)
    const interval = setInterval(fetchFarmData, 60000);

    return () => clearInterval(interval); // Clean up interval on component unmount
  }, []);

  // Filter last 2 days of data
  const getLastTwoDaysData = () => {
    if (!farmData || !Array.isArray(farmData)) return [];

    const twoDaysAgo = new Date();
    twoDaysAgo.setDate(twoDaysAgo.getDate() - 2); // Get date two days ago

    return farmData.filter((data) => new Date(data.timestamp) >= twoDaysAgo);
  };

  const filteredData = getLastTwoDaysData();

  // Prepare data for the graph
  const chartData = {
    labels: filteredData.map((data) =>
      new Date(data.timestamp).toLocaleString()
    ), // X-axis labels as timestamps
    datasets: [
      {
        label: 'Temperature (°C)',
        data: filteredData.map((data) => data.temperature), // Y-axis temperature values
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        fill: true,
        tension: 0.4,
      },
      {
        label: 'Soil Moisture (%)',
        data: filteredData.map((data) => data.soilMoisture),
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        fill: true,
        tension: 0.4,
      },
      {
        label: 'Humidity (%)',
        data: filteredData.map((data) => data.humidity),
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        fill: true,
        tension: 0.4,
      },
    ],
  };

  return (
    <div>
      {/* Navbar implementation */}
      <nav className="navbar">
        <div className="navbar-left">
          <img src="/images/logo.png" alt="Logo" className="navbar-logo" />
        </div>
        <ul className="navbar-center">
          <li>
            <Link to="/">About Us</Link>
          </li>
          <li>
            <Link to="/">Sensors</Link>
          </li>
          <li>
            <Link to="/farm-dashboard">Farm Data</Link> {/* Farm Data link */}
          </li>
        </ul>
        <div className="navbar-right">
          {isLoggedIn ? (
            <>
              <span className="navbar-username">{username}</span>{' '}
              {/* Display username */}
            </>
          ) : (
            <></>
          )}
        </div>
      </nav>

      {/* Cards Section */}
      <div className="row">
        <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
          <div className="card">
            <div className="card-header p-3 pt-2">
              <div className="icon icon-lg icon-shape shadow-dark text-center border-radius-xl mt-n4 position-absolute">
                <img
                  src="/assets/images/tempicon.png"
                  alt="Temperature Icon"
                  className="card-icon"
                />{' '}
                {/* Custom icon */}
              </div>
              <div className="text-end pt-1">
                <p className="text-sm mb-0 text-capitalize">
                  Latest Temperature Data
                </p>
                {/* Ensure latestTemperature state is displayed correctly */}
                <h4 className="mb-0">
                  {latestTemperature !== null
                    ? `${latestTemperature} °C`
                    : 'Loading...'}
                </h4>
              </div>
            </div>
            <hr className="dark horizontal my-0" />
            <div className="card-footer p-3">
              <p className="mb-0">
                <span className="text-success text-sm font-weight-bolder"></span>
                <b>Temperature</b>
              </p>
            </div>
          </div>
        </div>

        {/* Soil Moisture Card */}
        <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
          <div className="card">
            <div className="card-header p-3 pt-2">
              <div className="icon icon-lg icon-shape shadow-primary text-center border-radius-xl mt-n4 position-absolute">
                <img
                  src="/assets/images/soilicon.png"
                  alt="Soil Icon"
                  className="card-icon"
                />{' '}
                {/* Custom icon */}
              </div>
              <div className="text-end pt-1">
                <p className="text-sm mb-0 text-capitalize">
                  Latest Soil Moisture Data
                </p>
                {/* Ensure latestSoilMoisture state is displayed correctly */}
                <h4 className="mb-0">
                  {latestSoilMoisture !== null
                    ? `${latestSoilMoisture} %`
                    : 'Loading...'}
                </h4>
              </div>
            </div>
            <hr className="dark horizontal my-0" />
            <div className="card-footer p-3">
              <p className="mb-0">
                <span className="text-success text-sm font-weight-bolder"></span>
                <b>Soil Moisture</b>
              </p>
            </div>
          </div>
        </div>

        {/* Humidity Card */}
        <div className="col-xl-3 col-sm-6 mb-xl-0 mb-4">
          <div className="card">
            <div className="card-header p-3 pt-2">
              <div className="icon icon-lg icon-shape shadow-success text-center border-radius-xl mt-n4 position-absolute">
                <img
                  src="/assets/images/humidicon.png"
                  alt="Humidity Icon"
                  className="card-icon"
                />{' '}
                {/* Custom icon */}
              </div>
              <div className="text-end pt-1">
                <p className="text-sm mb-0 text-capitalize">
                  Latest Humidity Data
                </p>
                {/* Ensure latestHumidity state is displayed correctly */}
                <h4 className="mb-0">
                  {latestHumidity !== null
                    ? `${latestHumidity} %`
                    : 'Loading...'}
                </h4>
              </div>
            </div>
            <hr className="dark horizontal my-0" />
            <div className="card-footer p-3">
              <p className="mb-0">
                <span className="text-danger text-sm font-weight-bolder"></span>
                <b>Humidity</b>
              </p>
            </div>
          </div>
        </div>

        {/* Motion Detection Card */}
        <div className="col-xl-3 col-sm-6">
          <div className="card">
            <div className="card-header p-3 pt-2">
              <div className="icon icon-lg icon-shape shadow-info text-center border-radius-xl mt-n4 position-absolute">
                <img
                  src="/assets/images/motionicon.png"
                  alt="Motion Icon"
                  className="card-icon"
                />{' '}
                {/* Custom icon */}
              </div>
              <div className="text-end pt-1">
                <p className="text-sm mb-0 text-capitalize">
                  Latest Motion Detection Data
                </p>
                {/* Ensure latestMotionDetected state is displayed correctly */}
                <h4 className="mb-0">
                  {latestMotionDetected !== null
                    ? latestMotionDetected
                      ? 'Detected'
                      : 'None'
                    : 'Loading...'}
                </h4>
              </div>
            </div>
            <hr className="dark horizontal my-0" />
            <div className="card-footer p-3">
              <p className="mb-0">
                <span className="text-success text-sm font-weight-bolder"></span>
                <b>Motion Detection</b>
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Graph Section */}
      <div className="graph-section">
        <h2>Sensor Data (Past 2 Days)</h2>
        <Line data={chartData} />
      </div>

      {/* Table to display raw data */}
      <div className="raw-data-table">
        <h2>Farm Data Dashboard</h2>
        <ul>
          {farmData ? (
            Array.isArray(farmData) ? (
              farmData.map((data, index) => (
                <li key={index}>
                  <strong>Date:</strong>{' '}
                  {new Date(data.timestamp).toLocaleString()} |{' '}
                  <strong>Temperature:</strong> {data.temperature} °C |{' '}
                  <strong>Soil Moisture:</strong> {data.soilMoisture} % |{' '}
                  <strong>Humidity:</strong> {data.humidity} % |{' '}
                  <strong>Motion Detected:</strong>{' '}
                  {data.motionDetected ? 'Yes' : 'No'}
                </li>
              ))
            ) : (
              <li>
                <strong>Date:</strong>{' '}
                {new Date(farmData.timestamp).toLocaleString()} |{' '}
                <strong>Temperature:</strong> {farmData.temperature} °C |{' '}
                <strong>Soil Moisture:</strong> {farmData.soilMoisture} % |{' '}
                <strong>Humidity:</strong> {farmData.humidity} % |{' '}
                <strong>Motion Detected:</strong>{' '}
                {farmData.motionDetected ? 'Yes' : 'No'}
              </li>
            )
          ) : (
            <p>Loading...</p>
          )}
        </ul>
      </div>
    </div>
  );
};

export default FarmDashboard;
