import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // Import useNavigate and Link for navigation
import styles from './LoginForm.module.css'; // Assuming the CSS file is named LoginForm.module.css
import loginImage from '/Users/pcompany/Desktop/Sem_VII/ET/AgriDost/frontend/src/components/Auth/loginimage.jpg'; // Update image path

const LoginForm = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [emailError, setEmailError] = useState("");
    const [passwordError, setPasswordError] = useState("");
    const navigate = useNavigate(); // Initialize useNavigate

    const validateEmail = (email) => {
        const pattern = /^[^ ]+@[^ ]+\.[a-z]{2,3}$/;
        return pattern.test(email);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        let valid = true;

        if (!email) {
            setEmailError("Email can't be blank");
            valid = false;
        } else if (!validateEmail(email)) {
            setEmailError("Enter a valid email address");
            valid = false;
        } else {
            setEmailError("");
        }

        if (!password) {
            setPasswordError("Password can't be blank");
            valid = false;
        } else {
            setPasswordError("");
        }

        if (valid) {
            localStorage.setItem('email', email);
            localStorage.setItem('password', password);
            alert(`Welcome, ${email}!`);
            navigate('/');
        }
    };

    return (
        <div className={styles.container}>
            <div className={styles.leftSide}>
                <img src={loginImage} alt="Building" className={styles.loginImage} /> {/* Added className for specific styling */}
                <div className={styles.wrapper}>
                    <header>Login</header>
                    <form onSubmit={handleSubmit}>
                        <div className={`${styles.field} ${emailError ? styles.error : ""}`}>
                            <label htmlFor="email">Email Address</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="email"
                                    type="text" 
                                    placeholder="Enter your email" 
                                    value={email} 
                                    onChange={(e) => setEmail(e.target.value)} 
                                />
                                <i className={`icon fas fa-envelope ${styles.icon}`}></i>
                            </div>
                            {emailError && <div className={styles.errorTxt}>{emailError}</div>}
                        </div>
                        <div className={`${styles.field} ${passwordError ? styles.error : ""}`}>
                            <label htmlFor="password">Password</label>
                            <div className={styles.inputArea}>
                                <input 
                                    id="password"
                                    type="password" 
                                    placeholder="Enter your password" 
                                    value={password} 
                                    onChange={(e) => setPassword(e.target.value)} 
                                />
                                <i className={`icon fas fa-lock ${styles.icon}`}></i>
                            </div>
                            {passwordError && <div className={styles.errorTxt}>{passwordError}</div>}
                        </div>
                        <div className={styles.passTxt}>
                            <Link to="#">Forgot password?</Link>
                        </div>
                        <button type="submit">Login</button>
                    </form>
                    <div className={styles.signTxt}>
                        Not yet a member? <Link to="/signup">Signup now</Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginForm;
